mapper.update:
update club a
set
  a.club_pic = #{picUrl}
where a.club_id = #{club_id}
  and a.is_delete = 0

update user_detail a
set
  a.user_level = #{user_level}
where a.club_id = #{club_id}
  and a.user_id = #{id}

mapper.select:
select a.club_id ,a.club_name ,d.value as club_status from club a left join world_city b on a.id = b.id left join dictionary c on a.language_id = c.code and c.p_code='language' and c.language= #{language} left join dictionary d on a.club_status = d.code and d.p_code = 'club' and d.language= #{language} where is_delete = '0'
and a.club_name like CONCAT('%', #{other} ,'%')
limit #{pageStartRow } , #{pageRecorders }

