mapper.update:
update event
  set  event_status = #{status}
where event_id = #{eventID}

mapper.select:
select a.event_id ,a.club_id ,a.end_time ,a.event_address ,a.event_name ,a.event_status ,coalesce(a.fee,0.0) as fee ,a.form_item ,coalesce(a.image,'') as image ,coalesce(a.max_people,0) as max_people ,coalesce(a.min_people,0) as min_people ,a.organizer_id ,a.register_end_time ,a.register_start_time ,a.start_time ,a.view_guests ,event_detail as event_detail_data ,event_notice from event a
(
a.event_status = #{eventStatus}
and a.end_time > now() or ( a.event_status = 3 and a.end_time >= now() ) )
and a.event_id = #{event_id}
and a.club_id = #{club_id}
ORDER BY a.event_status asc,a.register_start_time asc,a.start_time asc

mapper.update:
update  event_registration
   set my_status = 0
 where event_id = #{eventID}
   and user_id = #{userID}
