mapper.select:
select vote_id ,vote_name ,image ,start_time ,end_time ,vote_status ,vote_index from vote
vote_id = #{voteID}
and vote_status in (
#{stat}
)

(select
    b.user_id
  from vote_range a,user_info b
  where a.range_type = 0
  and a.vote_id = #{voteID}
)
union all
(select
    b.user_id
  from vote_range a
  left join user_competition b
   on a.circle_id = b.competition_id
   and b.user_id = #{userID}
  where a.range_type = 1
  and a.vote_id = #{voteID}
)
union all
(select
    b.user_id
  from vote_range a
  left join user_detail b
   on a.circle_id = b.club_id
   and b.user_id = #{userID}
  where a.range_type = 2
  and a.vote_id = #{voteID}
)
union all
(select
    a.circle_id as user_id
  from vote_range a
  where a.range_type = 3
  and a.vote_id = #{voteID}
)
